import { Component, OnInit } from '@angular/core';
import { PlayerService } from '../player.service';
import { Player } from '../Player';

@Component({
  selector: 'app-player-list',
  templateUrl: './player-list.component.html',
  styleUrls: ['./player-list.component.css'],
})
export class PlayerListComponent implements OnInit {
  players: Player[] = []; // Define an array to store players

  constructor(private playerService: PlayerService) {}

  ngOnInit(): void {
    // Fetch the list of players when the component is initialized
    this.playerService.getPlayers().subscribe((players) => {
      this.players = players;
    });
  }
}

<!-- player-list.component.html -->

<h2>Player List</h2>

<ul>
  <li *ngFor="let player of players">
    <a [routerLink]="['/players', player.id]">{{ player.name }}</a>
    <button (click)="deletePlayer(player.id)">Delete</button>
  </li>
</ul>

<a [routerLink]="['/players/add']">Add Player</a>
